CID=$(oci iam compartment list --name tsupi --compartment-id-in-subtree true --query data[0].id --raw-output)
VID=$(oci network vcn list --compartment-id $CID --display-name vcn-spark --query data[0].id --raw-output)
SSMI=$(oci network subnet list --compartment-id $CID --vcn-id $VID --display-name spark-master --query data[0].id --raw-output)
SSWI=$(oci network subnet list --compartment-id $CID --vcn-id $VID --display-name spark-worker --query data[0].id --raw-output)
ADI=$(oci iam availability-domain list --compartment-id $CID --query data[0].name --raw-output)
SMCII=$(oci compute instance list --compartment-id $CID --display-name sm0ci --query data[0].id --raw-output)
SWCII=$(oci compute instance list --compartment-id $CID --display-name sw0ci --query data[0].id --raw-output)
SMPIP=$(oci compute instance list-vnics --compartment-id $CID --instance-id $SMCII --query 'data[0]."public-ip"' --raw-output)
SMPRIP=$(oci compute instance list-vnics --compartment-id $CID --instance-id $SMCII --query 'data[0]."private-ip"' --raw-output)
SWPIP=$(oci compute instance list-vnics --compartment-id $CID --instance-id $SWCII --query 'data[0]."private-ip"' --raw-output)
